  captchaLanguage?: Maybe<FieldWrapper<Scalars["String"]>>;
  /** Determines the theme to be used for the reCAPTCHA field. Only applicable to the recaptcha captcha type. */
  captchaTheme?: Maybe<FieldWrapper<CaptchaFieldThemeEnum>>;
  /** Determines the type of CAPTCHA field to be used. */
  captchaType?: Maybe<FieldWrapper<CaptchaFieldTypeEnum>>;
